document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll(".work__button").forEach(function(t){t.addEventListener("click",function(t){const e=t.currentTarget.dataset.path;document.querySelectorAll(".work__container").forEach(function(t){t.classList.remove("work__container-active")}),document.querySelector(`[data-target = "${e}"]`).classList.add("work__container-active")})})}),$(document).ready(function(){$(".nav__button").click(function(){$(".nav__button").toggleClass("nav__button_active"),$(".nav__list-mobile").toggleClass("nav__list-mobile-active")})});